document.addEventListener("DOMContentLoaded",()=>{getProductList()});const getProductList=()=>{fetch(apiGetUrl).then(n=>n.json()).then(n=>{loadDataInDom(n)}).catch(n=>{})},loadDataInDom=n=>{n.forEach((n,e)=>{let t;t=e%2==0?"section-r":"section-l";let i=`\n        <section class="${t}">\n          <div class="${t}__padding">\n            <div class="${t}__img" style="background-image: url(${n.imageUrl});"></div>\n          </div>\n            <div class="${t}__border">\n              <h2 class="${t}__title">${n.name}</h2>\n              <h3 class="${t}__subtitle">${priceFormat(n.price)} &euro;</h3>\n              <p class="${t}__description">${n.description}</p>\n              <div class="underlined-btn ${t}__btn">\n                <a\n                  class="underlined-btn__link"\n                  href="../product/index.html?id=${n._id}"\n                  >voir plus</a\n                >\n              </div>\n            </div>\n        </section>`,d=document.getElementById("debut");d.innerHTML=d.innerHTML+i})};